mapper.select:

SELECT COUNT(*) FROM SHOPPING_CART
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = ${memberNo}

mapper.update:
UPDATE SHOPPING_CART SET 
QUANTITY = ${quantity}
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = ${memberNo}

mapper.delete:
DELETE FROM SHOPPING_CART
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = ${memberNo}
